// src/AdminDashboard.js
import React, { useEffect, useState } from "react";

function AdminDashboard() {
  const [users, setUsers] = useState([]);
  const [search, setSearch] = useState("");

  // جلب قائمة المستخدمين من Flask API
  useEffect(() => {
    fetch("http://localhost:5000/api/users")
      .then((res) => res.json())
      .then((data) => {
        setUsers(data.users || []);
      });
  }, []);

  // دالة تحديث الـ XP
  const updateXP = (userId) => {
    const newXP = prompt("أدخل قيمة XP الجديدة:");
    if (newXP) {
      fetch("http://localhost:5000/api/update_xp", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ id: userId, xp: parseInt(newXP) }),
      })
        .then((res) => res.json())
        .then(() => {
          // إعادة جلب المستخدمين بعد التحديث
          refreshUsers();
        });
    }
  };

  // دالة إعادة ضبط المستخدم
  const resetUser = (userId) => {
    if (window.confirm("هل أنت متأكد أنك تريد إعادة ضبط هذا المستخدم؟")) {
      fetch("http://localhost:5000/api/reset_user", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ id: userId }),
      })
        .then((res) => res.json())
        .then(() => {
          refreshUsers();
        });
    }
  };

  // إعادة جلب قائمة المستخدمين
  const refreshUsers = () => {
    fetch(" http://localhost:3000")
      .then((res) => res.json())
      .then((data) => {
        setUsers(data.users || []);
      });
  };

  // فلترة المستخدمين بالاسم
  const filteredUsers = users.filter((u) =>
    u.username.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div style={{ padding: "20px" }}>
      <h1>لوحة تحكم الإدارة</h1>
      <input
        type="text"
        placeholder="ابحث عن مستخدم..."
        onChange={(e) => setSearch(e.target.value)}
        value={search}
      />
      <table style={{ width: "100%", marginTop: "10px" }}>
        <thead>
          <tr>
            <th>المعرف</th>
            <th>الاسم</th>
            <th>XP</th>
            <th>المستوى</th>
            <th>إجراءات</th>
          </tr>
        </thead>
        <tbody>
          {filteredUsers.map((user) => (
            <tr key={user.id}>
              <td>{user.id}</td>
              <td>{user.username}</td>
              <td>{user.xp}</td>
              <td>{user.level}</td>
              <td>
                <button onClick={() => updateXP(user.id)}>تعديل XP</button>
                <button onClick={() => resetUser(user.id)}>إعادة ضبط</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default AdminDashboard;
